--[[ DO NOT EDIT THE ACTIONS BELOW OR THE SCRIPT CAN STOP WORKING! ]]
--@name MS_checkLoc --@desc Returns to a specified waypoint or label on an optional waypoint section. --@param {number} or {string} wpt --@param {string} section --@returns {null}
function MS_checkLoc(wpt, section) section = section or $wptsection wpt = (type(wpt) == 'number') and ($wptid - wpt) or wpt --credits to @Raphael waitping() if $posz ~= $wptz then print('Failed reaching floor '..$wptz..'. Trying again.') gotolabel(wpt, section) end end
--@name MS_depositGold --@desc Deposits gold to the local bank --@returns {boolean}
function MS_depositGold() if islocation(1) then print('Depositing gold to the nearest bank NPC.') npctalk('hi', 'deposit all', 'yes', 'balance') wait(500,600) if gold() == 0 then print('Deposited gold sucessfully.') return true else print('Failed to deposit gold. Retrying.') gotolabel("Bank") return false end else gotolabel("Bank") return false end end
--@name MS_levitate --@desc Tries to walk to another floor, casting exani hur up/down if failing. --@param {string} side --@param {string} updown --@returns {boolean}
function MS_levitate(side, updown) local initialZ = $posz
move(side) wait(700,900) if $posz ~= initialZ then return true end if $posz == initialZ then turn(side) wait(300,500) cast('exani hur '..updown) if $posz ~= initialZ then return true end end return false end
