PersistentScript.script:
auto(500)
MainBP = {name = getuseroption("MainBP"), destination = "back"}
SuppBP = {name = getuseroption("SuppBP"), destination = getuseroption("MainBP")}
LootBP = {name = getuseroption("LootBP"), destination = getuseroption("MainBP")}
GoldBP = {name = getuseroption("GoldBP"), destination = getuseroption("MainBP")}
MainDpBp = {name = getuseroption("MainDpBp"), destination = "Depot Chest"}
StackBP = {name = getuseroption("StackBP"), destination = getuseroption("MainDpBp")}
RareBP = {name = getuseroption("RareBP"), destination = getuseroption("MainDpBp")}
RingBP = {name = getuseroption("RingBP"), destination = "Depot Chest"}


setlootingdestination("mainbp", getuseroption("MainBP"))
setlootingdestination("goldbp", getuseroption("GoldBP"))
setlootingdestination("suppbp", getuseroption("SuppBP"))
setlootingdestination("lootbp", getuseroption("LootBP"))
setlootingdestination("maindpbp", getuseroption("MainDpBp"))
setlootingdestination("stackbp", getuseroption("StackBP"))
setlootingdestination("rarebp", getuseroption("RareBP"))
setlootingdestination("ringbp", getuseroption("RingBP"))




-- gold coins

if $cap < 150 or not getuseroption("LootGold") then
   setsetting('Looting/LootList/gold coin/Action', 'Ignore')
else
   setsetting('Looting/LootList/gold coin/Action', 'Loot') 
end

auto(100)
local MonsterToGfb = {"Lizard High Guard", "Brimstone Bug", "Lancer Beetle", "Wailing Widow", "Mutated Bat", "Haunted Treeling", "Mutated Tiger", "Killer Caiman", "Mutated Rat", "Lizard Dragon Priest", "Lizard Legionnaire"} local MonsterToMStrike = {"Lizard High Guard", "Brimstone Bug"} local MonsterToSStrike = {"Lizard High Guard", "Brimstone Bug", "Wailing Widow", "Mutated Bat"} local MonsterToStrike = {"Lizard High Guard", "Brimstone Bug", "Lancer Beetle", "Wailing Widow", "Mutated Bat", "Haunted Treeling", "Mutated Tiger", "Killer Caiman", "Mutated Rat"}
if Self.CoolDown(SPELL_GROUP_ATTACK) == 0 and $wptsection == "Hunt" and getuseroption("AvaWalk") or Self.CoolDown(SPELL_GROUP_ATTACK) == 0 and $trapped or Self.CoolDown(SPELL_GROUP_ATTACK) == 0 and $wptsection == "Hunt" and not getuseroption("AvaWalk") and getsetting('Targeting/Enabled') == 'yes' then if ((shootarearune("avalanche rune", 2, unpack(MonsterToGfb))) or (maround_ib(5, "Brimstone Bug") > 2)) and (not getuseroption("pvp") or (paroundfloor(10, 15) == 0 and ($timems-$playerlastseen) >= 2000)) and itemcount("avalanche rune", 'all') > 0 and $wptsection == "Hunt" then pausewalking(500) shootarearune("avalanche rune", 1, "Brimstone Bug", "Wailing Widow", "Lizard High Guard") wait(100,200) elseif $attacked.id ~= 0 and $attacked.dist <= 3 then if getuseroption("MaxStrike") and $attacked.hppc >= 60 and $target.isshootable and cancastspell(getuseroption("MaxStrikeSpellName")) and table.find(MonsterToMStrike, $attacked.name) then cast(getuseroption("MaxStrikeSpellName")) wait(50,100) elseif getuseroption("StrongStrike") and $attacked.hppc >= 45 and $target.isshootable and cancastspell(getuseroption("StrongStrikeSpellName")) and table.find(MonsterToSStrike, $attacked.name) then cast(getuseroption("StrongStrikeSpellName")) wait(50,100) elseif cancastspell("exori frigo") and table.find(MonsterToStrike, $attacked.name) and $target.isshootable then cast("exori frigo") wait(50,100) end end end

auto(200) local CastUe = getuseroption("CastUe") local UeSpellName = getuseroption("UeSpellName")
--local local MonsterToGfb = {"Lizard High Guard", "Brimstone Bug", "Lancer Beetle", "Wailing Widow", "Mutated Bat", "Haunted Treeling", "Mutated Tiger", "Killer Caiman", "Mutated Rat", "Lizard Dragon Priest", "Lizard Legionnaire"}
local Monsters = { {NAME = "Lizard Legionnaire", HP = 1400, Weak = 0.1}, {NAME = "Lizard Dragon Priest", HP = 1450, Weak = 0.1}, {NAME = "Lizard High Guard", HP = 1800, Weak = 0.0}, {NAME = "Brimstone Bug", HP = 1300, Weak = 0.1}, {NAME = "Mutated Bat", HP = 900, Weak = 0.0}, }
local AvgUeHit = ((((($level/5) + ($mlevel*11))+50) + (((($level/5) + ($mlevel*5.5)) + 25))) / 2) local MaxUeHit = (($level/5) + ($mlevel*11))+50-80
local Amount = 0 local LizHgAmount = 0 local NormalAmount = 0 for CREATURE in Creature.GetAll("ms") do for _, Mobs in ipairs(Monsters) do if table.find({Mobs.NAME}, CREATURE:Name()) then local ShootUe = (AvgUeHit + AvgUeHit*Mobs.Weak) local ActualHp = ((Mobs.HP*CREATURE:HealthPercent())/100) if ActualHp <= ShootUe then if Spell.isOnSpellArea(CREATURE, "5x5", false) then NormalAmount = NormalAmount + 1 Amount = Amount + 1 if CREATURE:Name() == 'Lizard High Guard' then LizHgAmount = LizHgAmount + 1 end end elseif (ActualHp <= MaxUeHit) and Spell.isOnSpellArea(CREATURE, "5x5", false) then NormalAmount = NormalAmount + 1 end end end end
if CastUe then if cooldown(UeSpellName) <= 200 and $targeting then if cancastspell(UeSpellName) and (Amount >= getuseroption("UEA") or LizHgAmount >= 4 or NormalAmount >= 9) and (not getuseroption("pvp") or (paroundfloor(10, 15) == 0 and ($timems-$playerlastseen) >= 10000)) then cast(UeSpellName) wait(200) end end end

Waypoint.script:
if not islocation(2) then gotolabel("ToManaShop", $wptsection) else local SUPPLIES = { {NAME = getuseroption("ManaName"), AMOUNT = getuseroption("MpToBuy")}, {NAME = "avalanche rune", AMOUNT = getuseroption("AvaToBuy")}, {NAME = "sudden death rune", AMOUNT = getuseroption("SdToBuy")}, }
while (windowcount(SuppBP["name"]) == 0) do closewindows() waitping() openitem(MainBP["name"], 'back') waitping() openitem(SuppBP["name"], MainBP["name"], true) waitping() end opentrade() waitping() sellflasks()
for _, SUP in ipairs(SUPPLIES) do local TOTAL = (SUP.AMOUNT - itemcount(SUP.NAME))/100
for I=1, TOTAL do buyitems(SUP.NAME, SUP.AMOUNT) waitping() moveitems(SUP.NAME, SuppBP["name"], MainBP["name"], 100) waitping() TOTAL = TOTAL - 100 end
if (itemcount(SUP.NAME) < SUP.AMOUNT) then buyitems(SUP.NAME, SUP.AMOUNT - itemcount(SUP.NAME)) waitping() end
while (itemcount(SUP.NAME, MainBP["name"]) > 0) and (getcontainer(SuppBP["name"]).itemcount < getcontainer(SuppBP["name"]).maxcount) do moveitems(SUP.NAME, SuppBP["name"], MainBP["name"], 100) waitping() end end end
